.qplayer_tips{
  width:210px;
  height: 0;
  font-family:none !important;
  text-align:center;
  background:#9BCD9B;
  position:fixed;
  margin:30px;
  color: #fff;
  border-radius: 0.5em;
  font-size: 18px;
  font-weight: bold;
  z-index: 9999999;
}

/*箭頭下-邊框*/
.qplayer_tips .tips_arrow{
  border-width:0;
  border-style:solid;
  border-color:#9BCD9B transparent transparent transparent;
  position:absolute;
  bottom:-29px;
  left:17px;
}

.qplayer_tips .tips_button {
  height: 0;
  padding: 0;
  width: 58px;
  position: absolute;
  bottom: 7px;
  right: 7px;
  border:0;
  color: #fff;
  background-color: transparent;
  border:1px dashed rgba(0,0,0,0.15);
  border-radius: 5px;
  cursor: pointer;
  font-size: 10px;
  font-family:none !important;
}

.qplayer_tips .tips_button:hover {
    box-shadow: 0 0 10px #fff;
}

.qplayer_tips .info{
  padding: 20px;
  display: table;
}

data.styles:
.post-body h2 {
  border-bottom: 1px solid #eee;
  padding-bottom: 10px;
}
.post-body h3 {
  font-size: 1.2em;
}
.post-body .image-caption {
  font-size: 0.5em;
  font-weight: normal;
  margin: -10px auto 15px;
}
.post-body img {
  border: 1px solid #ddd;
  box-sizing: border-box;
  padding: 0px;
  border-radius: 3px;
  box-shadow: 4px 4px 4px 0px #ddd;
}
.reward-container button {
  background: #222;
  color: #f5f5f5;
  height: 38px;
  width: 38px;
  padding: 0 0 0;
  border: 0;
  border-radius: 5px;
